"use strict";window.addEventListener("DOMContentLoaded",function(){$.ajax({url:"../lib/listinfo.json",dataType:"json",success:function(i){console.log(i);var n="";i.forEach(function(i){n+='<div class="good_item">\n                    <div class="goodimg">\n                        <a href=""><img src="'.concat(i.image,'"></a>\n                        <div class="handler">\n                            <a href="javascript:;">加入收藏</a>\n                        </div>\n                    </div>\n                    <div class="yc"></div>\n                    <div class="goodinfo">\n                        <a href="">\n                            <span>').concat(i.info,'</span>\n                        </a>\n                        <div class="goods-info-detail">').concat(i.num1,'</div>\n                        <div class="goods-info-detail">').concat(i.num2,'</div>\n                    </div>\n                    <div class="goodprice">\n                        <div class="present-price">').concat(i.price,'</div>\n                        <div class="login-out" data-login=""></div>\n                        <div class="activity"></div>\n                    </div>\n                    <div class="goodstock">\n                        <div class="lack-stock">\n                           ').concat(i.date,'\n                        </div>\n                    </div>\n                    <div class="goodbuy">\n                        <div class="jian">-</div>\n                        <input type="text" class="goodnum" value="1">\n                        <div class="jia">+</div>\n                        <a href="javascript:void 0;" class="goods-order">订阅</a>\n                    </div>\n                </div>')}),$(".goodlist2").html(n)}})}),window.onload=function(){$(".goodlist2 .good_item .goodimg").mouseenter(function(){$(this).first().children().attr("src");$(this).next().css({display:"block"})}),$(".goodlist2 .good_item .goodimg").mouseleave(function(){$(this).next().css("display","none")})};